package Monitor;public class Queue {	private class elem {		private long info;		private elem next;	}	private elem head;	private elem tail;		public Queue () { 		head = null;		tail  = null;	}		public synchronized boolean empty () {		return (head == null);	}		public synchronized long first () {		return (head != null)? head.info: -1;	}		public synchronized void insert (long ID) {		elem p = new elem();		p.info = ID;		p.next = null;				if (head == null) head = tail = p;		else tail.next = p;	}		public synchronized long extract () {		long ID = -1;		if (head != null) {			ID = head.info;			head = head.next;			if (head == null) tail = null;				}		return ID;	}		public synchronized void print () {		if (head == null) System.out.print("Coda vuota\n");		else {			elem p = head;			System.out.print("Coda:\t");			while(p.next != null) {				System.out.print(p.info + ",\t");				p = p.next;			}			System.out.print(p.info + "\n");		}	}}	